import React from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

import {List, Header, Container, Segment} from 'semantic-ui-react';

class App extends React.Component{
  constructor(props){
    super(props)

    this.state = {
      "topics":[]
    }
  }

  componentDidMount(){
    axios.get('/topics')
    .then(x => x['data'])
    .then(x => this.setState({"topics":x}))
  }

  render() {
    console.log(this.state.topics)
    let topic_segments = []
    let i = 0;
    for (i; i < this.state.topics.length; i++){
      topic_segments.push(<Segment>this.state.topics[i]</Segment>)
    }
    return(
      <div>
        <List>
          <Segment><p>{this.state.topics[i]}</p></Segment>
        </List>

      </div>
    )
  }
}

export default App;
